// Function to initialize the video grid
function initVideoGrid() {
    const container = document.getElementById('videoContainer');
    if (!container) return;
    
    // Clear existing content
    container.innerHTML = '';
    
    // Add each video to the grid
    videoData.forEach((video, index) => {
        const videoCard = document.createElement('div');
        videoCard.className = 'video-card';
        videoCard.dataset.tags = video.tags.join(' ');
        
        // Add staggered animation
        videoCard.style.animationDelay = `${index * 0.1}s`;
        
        videoCard.innerHTML = `
            <div class="video-container">
                <div class="sp-embed-player" data-id="${video.id}">
                    <script src="https://go.screenpal.com/player/appearance/${video.id}"></scr`+`ipt>
                    <iframe 
                        width="100%" 
                        height="100%" 
                        style="border:0;" 
                        scrolling="no" 
                        src="https://go.screenpal.com/player/${video.id}?width=100%&height=100%&ff=1&title=0" 
                        allowfullscreen="true"
                        loading="lazy"
                    ></iframe>
                </div>
            </div>
            <div class="video-info">
                <h3 class="video-title">${video.title}</h3>
                <div class="video-meta">
                    <span>${new Date(video.date).toLocaleDateString()}</span>
                    ${video.isNew ? '<span class="new-badge">NEW</span>' : ''}
                </div>
            </div>
        `;
        
        container.appendChild(videoCard);
    });
}

// Initialize when the page loads
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initVideoGrid);
} else {
    initVideoGrid();
}
